import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";
import Form from "./Form";
import { Link } from "react-router-dom";

const LoginPage = () => {
  const theme = useTheme();
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");

  return (
    <Box>
      <Box
        width="100%"
        backgroundColor={theme.palette.background.alt}
        p="1rem 6%"
        textAlign="center"
      >
        <Link to="/" style={{ textDecoration: "none", color: "inherit" }}>
          <Typography fontWeight="bold" fontSize="32px" color="primary">
            Join The Waitlist
          </Typography>
        </Link>
      </Box>

      <Box
        sx={{
          position: "relative",
          width: isNonMobileScreens ? "50%" : "93%",
          p: "2rem",
          m: "2rem auto",
          borderRadius: "1.5rem",
          backgroundColor: theme.palette.background.alt,
          overflow: "hidden", // Prevents triangle overflow
        }}
      >
        {/* Triangle background */}
        <Box
          sx={{
            position: "absolute",
            top: 0,
            right: "-50px", // Positioning for the triangle
            width: 0,
            height: 0,
            borderTop: "100vh solid #2C654299", // Triangle color and height
            borderLeft: "200px solid transparent", // Triangle base width
            zIndex: -1, // Places triangle behind the form
          }}
        />

        {/* <Typography fontWeight="500" variant="h5" sx={{ mb: "1.5rem" }}>
          Welcome to Polan AI, the Scriptwriting tool for TV commercials!
        </Typography> */}

        <Form />
      </Box>
    </Box>
  );
};

export default LoginPage;
